mapper.select:
SELECT 
FROM permissions


        AND (name LIKE CONCAT('%', #{keyword}, '%') 
        OR code LIKE CONCAT('%', #{keyword}, '%')
        OR description LIKE CONCAT('%', #{keyword}, '%'))


        AND category = #{category}


        AND is_enabled = #{isEnabled}


ORDER BY sort_order ASC, id ASC

    LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM permissions
AND (name LIKE CONCAT('%', #{keyword}, '%') OR code LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
AND category = #{category}
AND is_enabled = #{isEnabled}

mapper.update:
UPDATE permissions
name = #{name}, code = #{code}, description = #{description}, resource = #{resource}, action = #{action}, category = #{category}, parent_id = #{parentId}, sort_order = #{sortOrder}, is_enabled = #{isEnabled}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE permissions 
SET is_enabled = #{isEnabled}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM permissions
WHERE code = #{code}

    AND id != #{excludeId}

SELECT COUNT(*)
FROM permissions
WHERE parent_id = #{parentId}
