mapper.update:
update user
username = #{username}, password = #{password}, gender = #{gender}, email = #{email}, avatar = #{avatar}, phone = #{phone}, address = #{address}, age = #{age}, bio = #{bio}, status = #{status},
where uid = #{uid}

mapper.select:
