select count(*) from user u

    join user_role ur on u.uid = ur.user_id
    where ur.role_id = #{roleId}

        AND (u.username like CONCAT('%', #{keyword}, '%') 
        OR u.email like CONCAT('%', #{keyword}, '%') 
        OR u.phone like CONCAT('%', #{keyword}, '%'))


        AND u.status = #{status}


        AND u.gender = #{gender}





            (u.username like CONCAT('%', #{keyword}, '%') 
            OR u.email like CONCAT('%', #{keyword}, '%') 
            OR u.phone like CONCAT('%', #{keyword}, '%'))


            AND u.status = #{status}


            AND u.gender = #{gender}

SELECT u.*
FROM user u
JOIN user_role ur ON u.uid = ur.user_id
WHERE ur.role_id = #{roleId}
ORDER BY u.uid ASC
LIMIT #{offset}, #{pageSize}

SELECT u.*, r.id as role_id, r.name as role_name, r.description as role_description, r.code as role_code, r.status as role_status FROM user u LEFT JOIN user_role ur ON u.uid = ur.user_id LEFT JOIN role r ON ur.role_id = r.id
AND u.username LIKE CONCAT('%', #{username}, '%')
AND u.email LIKE CONCAT('%', #{email}, '%')
AND u.status = #{status}
AND r.id = #{roleId}
ORDER BY u.uid ASC LIMIT #{offset}, #{pageSize}

SELECT COUNT(DISTINCT u.uid) FROM user u LEFT JOIN user_role ur ON u.uid = ur.user_id LEFT JOIN role r ON ur.role_id = r.id
AND u.username LIKE CONCAT('%', #{username}, '%')
AND u.email LIKE CONCAT('%', #{email}, '%')
AND u.status = #{status}
AND r.id = #{roleId}
